import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { GoogleLogin } from '@react-oauth/google';
import { useNavigate } from 'react-router-dom';
import ReactiveCursor from '../components/ReactiveCursor';
import './Login.css';

const Login = () => {
    const navigate = useNavigate();
    const [error, setError] = useState('');

    useEffect(() => {
        const userInfo = localStorage.getItem('userInfo');
        if (userInfo) {
            navigate('/');
        }
    }, [navigate]);

    const handleGoogleSuccess = async (credentialResponse) => {
        try {
            const { credential } = credentialResponse;
            // Using the backend running at localhost:5000
            const res = await axios.post('http://localhost:5000/api/users/google-login', {
                tokenId: credential,
            });

            localStorage.setItem('userInfo', JSON.stringify(res.data));
            navigate('/');
        } catch (err) {
            console.error(err);
            setError(err.response?.data?.message || 'Google Login Failed');
        }
    };

    const handleError = () => {
        setError('Google Sign In was unsuccessful. Try again later');
    };

    return (
        <div className="login-container">
            <ReactiveCursor />

            {/* Background ambience */}
            <div className="bg-circle bg-circle-1"></div>
            <div className="bg-circle bg-circle-2"></div>

            <div className="login-card">
                <h1>Analytics Pro</h1>
                <p>Sign in to unlock business insights</p>

                {error && <div className="error-message">{error}</div>}

                <div className="google-login-wrapper">
                    <GoogleLogin
                        onSuccess={handleGoogleSuccess}
                        onError={handleError}
                        theme="filled_black"
                        shape="pill"
                        size="large"
                        width="300"
                        text="continue_with_google"
                    />
                </div>
            </div>
        </div>
    );
};

export default Login;
